import React from 'react'
import EndInfo from '../LoginPage/EndInfo'
import "./NewUser.css"
import {useNavigate} from 'react-router-dom'
import {createUserWithEmailAndPassword } from 'firebase/auth'
import {auth} from '../../firebase-config' 


function NewUser() {
  const [email, setEmail] = useState('');
  const [password,setPassword] = useState('')
  const handleSubmit = (event) => {
    event.preventDefault();
    register()
    navigate('/home');
  };

  const register = async () =>{
    try{
      const user= await createUserWithEmailAndPassword(
        auth, email , password
      );
        console.log(user);
    } catch (error) {
      console.log(error.message)
    }
};

  return (
    <div className='register'>
        <div className='registerNav'>
              <div> <a href="nil"><img className="nflogo" src="https://image.tmdb.org/t/p/original/wwemzKWzjKYJFfCeiB57q3r4Bcm.svg" alt="nf-logo" /></a>
              <a href="/" className='signin-reg' >Sign In</a></div>
              <div className='smallLine'></div>
        </div>
        <form className='content-reg' onSubmit={handleSubmit}>
            <div className='contents-reg'>
              <h1 classname="heading-register">Enter your Email and Password</h1>
              <h3 className='mini-head'>Just a few more steps and you're done!</h3>
              <h3 className='mini-head'>We hate paperwork, too.</h3>
              <input type="email" className='reg-email' placeholder='Enter Email'value={email} onChange={(event) => setEmail(event.target.value)} required></input>
              <input type="password" className='reg-password'  placeholder='Enter Password'></input>
              <button type="submit" className="reg-submit">Create Account</button>
            </div>
        </form>
        <EndInfo backgroundColor={"white"}/>
    </div>
  )
}

export default NewUser
