import React from 'react'
import "./Navbar.css"
import{useState,useEffect} from "react"
function Navbar() {

    const [show, setShow] = useState(false)
    const controlNavbar = () => {
        if (window.scrollY > 400 ) {
            setShow(true)
        }else{
          setShow(false)
        }
    }

    useEffect(() => {
        window.addEventListener('scroll', controlNavbar)
        return () => {
            window.removeEventListener('scroll', controlNavbar)
        }
    }, [])
  return (
    <div className={`navbar ${show && 'navbar-hidden'}`}>
        <a href="https://www.netflix.com/in/"><img className="navlogo" src="https://image.tmdb.org/t/p/original/wwemzKWzjKYJFfCeiB57q3r4Bcm.svg" alt="Netflix-logo" /></a>
        <a className='menu' href='http://localhost:3000/'>Home</a>
        <a className='menu' href='http://localhost:3000/'>TV shows</a>
        <a className='menu'href='http://localhost:3000/'>Movies</a>
        <a className='menu'href='http://localhost:3000/'>My List</a>
        <a className='menu'href='http://localhost:3000/'>Browse by Languages</a>
        <img className="avatar" src="https://upload.wikimedia.org/wikipedia/commons/0/0b/Netflix-avatar.png" alt="Netflix-avatar" />

    </div>
  )
}

export default Navbar
