  const [movie, setMovie] = useState()
  const[urlId,setUrlId]= useState('')

  useEffect(() => {
    axios.get(props.url).then((response)=>{  
      setMovie(response.data.results[Math.floor(Math.random() * response.data.results.length)])
    })
  }, [props.url])

