        const user= await signInWithEmailAndPassword(
          auth, signinEmail , signinPassword
        );
        console.log(user);
      } catch (error) {
        console.log(error.message)

      }
  };*/




  return (
    <div className='page2content'>
        <div className='fade3'>
        <a href="nil"><img className="navlogopage2" src="https://image.tmdb.org/t/p/original/wwemzKWzjKYJFfCeiB57q3r4Bcm.svg" alt="Netflix-logo" /></a>

        <div className='contentbox'>
            <form className='page2signin' onSubmit={handleSubmit}>
                <h3 className='page2head1'>Sign In</h3>
                <input type="email" className='inputpage2' value={email} onChange={(event) => setEmail(event.target.value)} required/>
                <label className='placeholderpage2'>Email or phone number</label>
                <input type="password" className="password" value={password} onChange={(event) => setPassword(event.target.value)} required/>
                <label className='passwordplaceholder'>Enter Password</label>
                <button type="submit" className='signinacc' >Sign In </button>

            </form>
        </div>
        <div className='dark'>
        <p className='contact-page2'>Questions? Call<a className='contactnum-page2' href=' xxxxxx'> 000-800-919-1694</a></p>
        <grid className='grid-container-page2'>

                <a class="grid-item" href="xx">FAQ</a>
                <a class="grid-item"href="xx">Help Centre</a>
                <a class="grid-item"href="xx">Terms of Use</a>
                <a class="grid-item"href="xx">Privacy</a>
                <a class="grid-item"href="xx">Cookie Preferences</a>
                <a class="grid-item"href="xx">Corporate Information</a>
        </grid>
        </div>

        </div>
    </div>
  )
}

export default CreateAccount
