import "./Login.css"
import React, { useState } from 'react';
import '../../node_modules/boo'

  function Login() {
    const [email, setEmail] = useState('');
    const [error, setError] = useState("");

    const handleSubmit = (event) => {
      event.preventDefault();
      console.log(`Email: ${email}`)
      if (!email) {
        setError("Email is required");
      } else {
        setError("");
        // perform other actions here (e.g. sending the email)
      }
    };
  return (
    <div>
        <div className='backgroundLogin'>
            <div className='navbar'>
                <a href="nil"><img className="navlogo" src="https://image.tmdb.org/t/p/original/wwemzKWzjKYJFfCeiB57q3r4Bcm.svg" alt="Netflix-logo" /></a>
                 <button className='signin'>Sign In</button>
                 <div class="dropdown">
                      <button
                          class="btn btn-primary dropdown-toggle"
                          type="button" 
                          id="dropdownMenuButton"
                          data-mdb-toggle="dropdown"
                          aria-expanded="false">English
                      </button>
                          <ul class="dropdown-menu" aria-labelledby="dropdownMenuButton">
                          <li><a class="dropdown-it" href="#">Hindi</a></li>
                          </ul>
                  </div>
            </div>
            <div className='content'>
                <pre className='heading1'>Unlimited movies, TV <br/>    shows and more.</pre> 
                <h2 className='heading2'> Watch anywhere. Cancel anytime.</h2>
                <h3 className='heading3'> Ready to watch? Enter your email to create or restart your membership.</h3>     
            </div>
            <form  className='userdata' onSubmit={handleSubmit}>
                <input type="email" className='input' value={email} onChange={(event) => setEmail(event.target.value)}required/>
                <label className='placeholder'>Email address</label>
                <button type="submit" className='submit' >Get Started ></button>
            </form>


        </div>
    </div>

  )
}

export default Login
