import React from 'react';
import LoginSubmit from "./LoginSubmit";
import Head3 from "./Head3";
import {useNavigate} from 'react-router-dom'
  function Login() {

    const navigate = useNavigate();
    const handleNavigate = (()=>{
      navigate('/signin');
    })

    const lang1 = "English"
    const lang2 = "Hindi"
  function lang(){
      return   lang1

  }

  return (
    <div>
        <div className='backgroundLogin'>
            <div className='navbar1'>
                <a href="nil"><img className="navlogo1" src="https://image.tmdb.org/t/p/original/wwemzKWzjKYJFfCeiB57q3r4Bcm.svg" alt="Netflix-logo" /></a>
                 <button className='signin-login' onClick={handleNavigate} >Sign In</button>
                 <div class="dropdownmain">
                      <button className="dropdownbuttonmain" >  </button>
                           <ul class="dropdown-menumain" id="langDrop">
                               <button  onClick = {lang1} class="dropdown-lang1main" >English</button><br/>
                               <button onClick= {lang2} class="dropdown-lang2main">हिंदी</button>
                          </ul>
                    </div>
            </div>
            <div className='content-login'>
                <pre className='heading1'>Unlimited movies, TV <br/>    shows and more.</pre> 
                <h2 className='heading2'> Watch anywhere. Cancel anytime.</h2>
                <Head3/>
            </div>

               <LoginSubmit/>

        </div>
    </div>

  )
}

export default Login
