import React, { useState ,useEffect } from 'react'
import "./Banner.css"
import axios from '../constants/axios'
import {imageUrl } from '../constants/constants'

function Banner (props) {
  const [movie, setMovie] = useState();
  const[urlId,setUrlId]= useState('');


  useEffect(() => {
    axios.get(props.url).then((response)=>{  
      setMovie(response.data.results[Math.floor(Math.random() * response.data.results.length)])
    }).catch(err=>{
      alert('Network Error')
    })
  }, [props.url])

  const handleMovie =(id)=>{
    axios.get(`/movie/${id}/videos?api_key=${API_KEY}&language=en-US`).then(response=>{
      if(response.data.results.length!==0){
        setUrlId(response.data.results[0])
      } 
        else{
        console.log("Array not available")
      }
    })
  }


  return (
    <div 
    style={{backgroundImage: `url(${movie? imageUrl+movie.backdrop_path : ""})`}}
    className="banner">
        <div className="content">
            <h1 className="movie">{movie ? movie.original_title?movie.original_title : movie.name:"nil"}</h1>
            <h1 className='description'>{movie ? movie.overview : "nil"}</h1>
            <div className="banner-buttons">
                <button className="button-play"onClick={movie?console.log(movie):'nil'}>Play</button>
                <button className="button-info">More Info</button>
            </div>
        </div>
        <div className="fade"></div>
    </div>
  )
}

export default Banner
