import './Collapsible.css'
function Collapsible(props) {

  let isClicked=true;
  const displayInfo=()=>{
    if(isClicked){
    document.querySelector(".paracontainer").style = "display:block "
    isClicked=false;
    }
    else{
      document.querySelector(".paracontainer").style = "display:none"
      isClicked=true;
    }
    }
  return (
    <div className='collapse'>
        {/* <button type="radio" onClick={displayInfo} className='collapsebutton'>{props.title}</button> */}
        <div className='paracontainer'>
        <p className='paragraph'>{props.data}</p>
          </div>
    </div>
  )
}
export default Collapsible
