import React from 'react'
import './Collapsible.css'
function Collapsible() {

    document.querySelectorAll('.collapse-button').forEach(button =>{
        button.addEventListener('click', () =>{
            const paraContainer = button.nextElementSibling;
            button.classList.toggle('collapse_button:active');
            if(button.classList.contains('collapse_button--active')){
                paraContainer.style.maxHeight = paraContainer.scrollHeight + 'px';
            }
            else{
                paraContainer.style.maxHeight = 0;

            }
        });
    });
  return (
    <div className='collapse'>
        <button type="button" className='collapse_button'>Click to open</button>
        <div className='para-container'>
        <p className='paragraph'>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do 
        eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis
         nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute
          irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla 
          pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia 
          deserunt mollit anim id est laborum.</p>
          </div>
    </div>
  )
}
export default Collapsible
