import React , { useState }from 'react'
import '../Loginpage/LoginSubmit.css'
import {useNavigate} from 'react-router-dom'
function LoginSubmit() {
    const [email, setEmail] = useState('');
    const navigate = useNavigate();

    const handleSubmit = (event) => {
      event.preventDefault();
      console.log(`Email: ${email}`)
      navigate('/home');
    };
  return (
    <div>
        <form  className='userdata' onSubmit={handleSubmit}>
                <input type="email" className='input' value={email} onChange={(event) => setEmail(event.target.value)} required oninvalid="this.setCustomValidity('Please enter your name');"/>
                <label className='placeholder'>Email address</label>
                <button type="submit" className='submit' >Get Started ></button>
            </form>
    </div>
  )
}

export default LoginSubmit
