import React from 'react'
import "./Navbar.css"
import {useNavigate} from 'react-router-dom'

import{useState,useEffect} from "react"
import {auth} from "../../firebase-config"
import {signOut ,onAuthStateChanged} from "firebase/auth"
function Navbar() {

  const navigate = useNavigate();
  const [user,setUser] = useState({})

  onAuthStateChanged(auth,(currentUser)=>{
    setUser(currentUser);
  })

  const logout = async() =>{
      await signOut(auth);
      console.log("signedout")
      navigate('/signin');
  }

    const [show, setShow] = useState(false)
    const controlNavbar = () => {
        if (window.scrollY > 350 ) {
            setShow(true)
        }else{
          setShow(false)
        }
    }

    useEffect(() => {
        window.addEventListener('scroll', controlNavbar)
        return () => {
            window.removeEventListener('scroll', controlNavbar)
        }
    }, [])

    const displayUser=()=>{
      document.querySelector(".signout").style = {
        top : -71
      }
      };

  return (
    <div className={`navbar ${show && 'navbar-hidden'}`}>
        <a href="x"><img className="navlogo" src="https://image.tmdb.org/t/p/original/wwemzKWzjKYJFfCeiB57q3r4Bcm.svg" alt="Netflix-logo" /></a>
        <a className='menu' href='x' >Home</a>
        <a className='menu' href='x'>TV shows</a>
        <a className='menu'href='x'>Movies</a>
        <a className='menu'href='x'>My List</a>
        <a className='menu'href='x'>Browse by Languages</a>
        <button className="avatar" onClick={displayUser}/>
        <button onClick={logout}  className='signout'>Sign Out Of <br/>
        <div className='userLoggedIn'>{user.email}</div></button>
    </div>

  )
}

export default Navbar
